//============================================================================//

var print = console.log;

var addEmail = require('../addEmail');

//============================================================================//

module.exports = function(PEODB) {
  PEODB.prototype.addEmail = function(args, cb) {
    var peodb = this;
    peodb.dbSession(args, function(db, cb) {
      return addEmail(db, args, cb);
    }, cb);
  }
}

