var print = console.log;

//============================================================================//

module.exports = function(PEODB) {

  var requiredArgs = [
    'company_id',
    'user_id',
  ];

  PEODB.prototype.addLiaison = function(args, cb) {
    var peodb = this;

    var missingArg = peodb.checkArgs(requiredArgs, args);
    if (missingArg) {
      return cb(Error('missing required argument: ' + missingArg));
    }

    var db = peodb.sharedDB;

    var qStr =
      'INSERT INTO company_liaisons ('
    + '  company_id, user_id'
    + ') VALUES ('
    +       db.escape(args.company_id)
    + ',' + db.escape(args.user_id)
    + ')'
    ;

    db.queryData(qStr, cb);
  }
}

