var print = console.log;

//============================================================================//

module.exports = function(PEODB) {

  PEODB.prototype.getPartTypes = function(cb) {
    var peodb = this;

    getPartTypes(peodb.sharedDB, cb);
  }

  var qStr =
    'SELECT part_type_id, name, abbr, primary_type FROM part_types';

  function getPartTypes(db, cb) {
    db.queryData(qStr, function(err, data) {
      return cb(err, data);
    });
  }
}

