//============================================================================//

var print = console.log;

//============================================================================//

var queryBasis =
  'SELECT'
  + '     role_type_id'
  + '   , brief         as role_type_brief'
  + '   , description   as role_type_desc'
  + '   , key_role      as role_type_key_role'
  + '   , `single_role` as role_type_single_role'
  + '   , display_order as role_type_display_order'
  + ' FROM'
  + '   role_types'
  ;

module.exports = function(PEODB) {

  PEODB.prototype.getRoleTypes = function(args, cb) {
    var peodb = this;

    if (typeof(args) === 'function') {
      cb = args;
      args = {};
    }

    return peodb.sharedDB.queryData(queryBasis, cb);
  }
}

