var print = console.log;

//============================================================================//

module.exports = function(PEODB) {

  PEODB.prototype.getStepTypes = function(cb) {
    var peodb = this;

    getStepTypes(peodb.sharedDB, cb);
  }

  var qStr =
    'SELECT step_type_id, step_abbr, step_name FROM step_types';

  function getStepTypes(db, cb) {
    db.queryData(qStr, function(err, data) {
      return cb(err, data);
    });
  }
}

