var print = console.log;

//============================================================================//

module.exports = function(PEODB) {

  var qStr =
    'SELECT'
  + '    tp_stage_id'
  + '    , stage'
  + '    , stage_num'
  + '    , abbr'
  + '    , description'
  + '  FROM'
  + '    tp_stages'
  ;

  PEODB.prototype.getTestStages = function(args, cb) {
    var peodb = this;

    if (typeof(args) === 'function') {
      cb = args;
    }

    peodb.sharedDB.queryData(qStr, cb);
  }
}

