module.exports = function(PEODB) {

  var qStr =
    'SELECT'
  + '    ate_type_id'
  + '    , brief'
  + '    , description'
  + '    , abbr'
  + '    , svn_root'
  + '    , company_id'
  + '  FROM'
  + '    ate_types'
  ;

  PEODB.prototype.getTesterTypes = function(args, cb) {
    var peodb = this;

    if (typeof(args) === 'function') {
      cb = args;
    }

    peodb.sharedDB.queryData(qStr, cb);
  }
}

