#!/usr/bin/env node

//============================================================================//

var print   = console.log;
var sprintf = require('printf');
var printf  = function() { print(sprintf.apply(null, arguments)); }

//============================================================================//

var fs   = require('fs');
var path = require('path');
var util = require('util');

var color = require('colors');

var sprintf = require('printf');

var async = require('async');

var dbConf = require('../../peodb.conf')
var db = require('gennum-mysql').createClient(dbConf);

var assert = require('assert');

//============================================================================//

db.queryData('select * from users limit 4', function(err, data) {
  assert.ifError(err);
  print('data',                         data);
  print('data.fieldMap',                data.fieldMap);
  print('data.rows',                    data.rows);
  print('data.rows[0]',                 data.rows[0]);
  print('data.rows[0].toMap()',         data.rows[0].toMap());
  print('data.rows[0].toMap(true)',     data.rows[0].toMap(true));
  print('data.toArray()',               data.toArray());
  print('data.toArray(1)',              data.toArray(1));
  print('data.toArray("name")',         data.toArray("name"));
  print('data.toMaps()',                data.toMaps());
  print('data.toMaps(true)',            data.toMaps(true));
  print('data.toMapMaps("name")',       data.toMapMaps("name"));
  print('data.toMapMaps("name", true)', data.toMapMaps("name", true));
  print('data.toMapArrays("name")',     data.toMapArrays("name"));
  print('JSON.stringify(data)',         JSON.stringify(data));
  db.end();
});

//============================================================================//

