################################################################################
#
#  PRU Database Documentation Table
#

CREATE TABLE `dbdoc` (
  `dbdoc_id`            INT UNSIGNED NOT NULL AUTO_INCREMENT PRIMARY KEY
, `table_name`          VARCHAR(64) -- NULL for DB descriptions
, `field_name`          VARCHAR(64) -- NULL for table descritpions
, `description`         TEXT NOT NULL
, UNIQUE KEY(`table_name`, `field_name`)
);

INSERT INTO dbdoc(`table_name`, `field_name`, `description`) VALUES
  (NULL,    NULL,
    "PEODB (Product Engineering Operations Database)")

, ("dbdoc", NULL,
    "database documentation table:

  table_name  field_name  description
  ----------  ----------  -----------
  NULL        NULL        documents the database
  name        NULL        documents the `name` table
  name1       name2       documents the `name1`.`name2` table field
  NULL        name        documents the `name` function or routine")

, ("dbdoc", "table_name",
    "name of the table being documented")

, ("dbdoc", "field_name",
    "name of the field, function, or routine being documented")
;

