################################################################################
#
#  Users  (Gennum employees, identified by their LDAP employeeNumber field)
#

CREATE TABLE `loadboards` (
  `loadboard_id`        INT UNSIGNED NOT NULL PRIMARY KEY
, `object_id`           INT UNSIGNED NOT NULL
, `serial_number`       VARCHAR(32)  NOT NULL
, `comments`            TEXT
,  UNIQUE  KEY (`serial_number`)
,  FOREIGN KEY (`object_id`) REFERENCES `objects` (`object_id`)
);

INSERT INTO dbdoc(`table_name`, `field_name`, `description`) VALUES
  ("loadboards", NULL,            "ATE loadboards")
, ("loadboards", "loadboard_id",  "primary key")
, ("loadboards", "object_id",     "base object reference")
, ("loadboards", "serial_number", "serial number, ex: 002412-013-01-00-01")
, ("loadboards", "comments",      "comment relating to the loadboard")
;

################################################################################

CREATE TABLE `tp_rev_loadboards` ( -- TIE-TABLE
  `tp_rev_id`           INT UNSIGNED NOT NULL
, `loadboard_id`        INT UNSIGNED NOT NULL
, PRIMARY KEY (`tp_rev_id`, `loadboard_id`)
, FOREIGN KEY (`tp_rev_id`)    REFERENCES `tp_revs`    (`tp_rev_id`)
, FOREIGN KEY (`loadboard_id`) REFERENCES `loadboards` (`loadboard_id`)
);

