################################################################################
#
#  Signoffs
#

CREATE TABLE `signoffs` (
  `signoff_id`          INT UNSIGNED NOT NULL AUTO_INCREMENT PRIMARY KEY
, `role_type_id`        INT UNSIGNED NOT NULL
, `user_id`             INT SIGNED                     -- NULL, until signed
, `state`               ENUM('PASS', 'FAIL', 'WAIVER') -- NULL, until signed
, `signoff_time`        DOUBLE -- TIME_T               -- NULL, until signed
, `stale`               BOOLEAN      NOT NULL DEFAULT FALSE
, `active`              BOOLEAN      NOT NULL DEFAULT TRUE
, `comments`            TEXT
, FOREIGN KEY (`user_id`)      REFERENCES `users`      (`user_id`)
, FOREIGN KEY (`role_type_id`) REFERENCES `role_types` (`role_type_id`)
);


################################################################################
